import React, { useState } from 'react';
import axios from 'axios';

function QuestionForm() {
  const [topic, setTopic] = useState('');
  const [questions, setQuestions] = useState([{ question: '', nestedQuestions: [''] }]);

  const handleTopicChange = (event) => {
    setTopic(event.target.value);
  };

  const handleQuestionChange = (index, event) => {
    const newQuestions = [...questions];
    newQuestions[index].question = event.target.value;
    setQuestions(newQuestions);
  };

  const handleNestedQuestionChange = (questionIndex, nestedQuestionIndex, event) => {
    const newQuestions = [...questions];
    newQuestions[questionIndex].nestedQuestions[nestedQuestionIndex] = event.target.value;
    setQuestions(newQuestions);
  };

  const handleAddQuestion = () => {
    const newQuestions = [...questions, { question: '', nestedQuestions: [''] }];
    setQuestions(newQuestions);
  };

  const handleAddNestedQuestion = (questionIndex) => {
    const newQuestions = [...questions];
    newQuestions[questionIndex].nestedQuestions.push('');
    setQuestions(newQuestions);
  };

  const handleSubmit = () => {
    const data = { topic, questions };
    axios.post('/api/questions', data)
      .then(response => console.log(response))
      .catch(error => console.log(error));
  };

  return (
    <div className="question-form-container">
      <div className="topic-field">
        <label>Topic</label>
        <input type="text" value={topic} onChange={handleTopicChange} />
      </div>
      {questions.map((question, questionIndex) => (
        <div key={questionIndex} className="question-container">
          <div className="question-field">
            <label>Question</label>
            <input
              type="text"
              value={question.question}
              onChange={(event) => handleQuestionChange(questionIndex, event)}
            />
          </div>
          {question.nestedQuestions.map((nestedQuestion, nestedQuestionIndex) => (
            <div key={nestedQuestionIndex} className="nested-question">
              <div className="question-field">
                <label>Nested Question</label>
                <input
                  type="text"
                  value={nestedQuestion}
                  onChange={(event) => handleNestedQuestionChange(questionIndex, nestedQuestionIndex, event)}
                  className="nested-question-field"
                />
              </div>
            </div>
          ))}
          <button className="add-nested-question" onClick={() => handleAddNestedQuestion(questionIndex)}>
            Add Nested Question
          </button>
        </div>
      ))}
      <div className="add-question-button-container">
        <button className="add-question-button" onClick={handleAddQuestion}>
          Add Question
        </button>
      </div>
      <div className="submit-button-container">
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
}

export default QuestionForm;
