import React, { useState, useEffect } from 'react';

const API_URL = "http://localhost:5001/fetchalldata";

const Tree = () => {
  const [jsonData, setJsonData] = useState({});
  const [expandedBranches, setExpandedBranches] = useState({});

  useEffect(() => {
    const fetchJsonData = async () => {
      const response = await fetch(API_URL);
      const data = await response.json();
      setJsonData(data);
    };
    fetchJsonData();
  }, []);

  const handleToggleExpand = (key) => {
    setExpandedBranches((prevState) => {
      return {
        ...prevState,
        [key]: !prevState[key]
      };
    });
  };

  const renderTreeNode = (node, key, isChildNode = false) => {
    const hasChildren = typeof node === 'object' && node !== null && Object.keys(node).length > 0;
    const isExpanded = expandedBranches[key];
  
    if (!hasChildren) {
      return (
        <li key={key}>
          <div className="node value">{key}: {node.toString()}</div>
        </li>
      );
    }
  
    return (
      <li key={key}>
        <div className={`node ${isChildNode ? 'child-node' : 'parent-node'}`}>
          {!isChildNode && (
            <button className={`expand-button ${isExpanded ? 'expanded' : ''}`} onClick={() => handleToggleExpand(key)}>{isExpanded ? '-' : '+'}</button>
          )}
          {isChildNode ? <span className="node-icon">â€¢</span> : null}
          <span className="node key">{key}:</span>
          {typeof node === 'object' ? '{' : ''}
        </div>
        {isExpanded && (
          <ul>
            {Object.entries(node).map(([childKey, childNode]) =>
              renderTreeNode(childNode, childKey, true)
            )}
          </ul>
        )}
        <div className={`node ${isChildNode ? 'child-node' : 'parent-node'}`}>
          {typeof node === 'object' ? '}' : ` ${node.toString()}`}
        </div>
      </li>
    );
  };
  
  return (
    <ul>
      {Object.entries(jsonData).map(([key, node]) => renderTreeNode(node, key))}
    </ul>
  );
};

export default Tree;
