import React from 'react';

const Flowchart = ({data}) => {
  return (
    <div className="flowchart-container">
      <div className="start-node">{data.symptom}</div>
      {data.questions.map((question, index) => {
        return (
          <div key={`question-${index}`} className="question-node">
            <div className="question">{question.ques}</div>
            {question.condition.yes.length > 0 && (
              <div className="branch-yes">
                {question.condition.yes.map((nestedQuestion, nestedIndex) => {
                  return (
                    <div key={`nested-question-yes-${nestedIndex}`} className="nested-question">{nestedQuestion}</div>
                  );
                })}
              </div>
            )}
            {question.condition.no.length > 0 && (
              <div className="branch-no">
                {question.condition.no.map((nestedQuestion, nestedIndex) => {
                  return (
                    <div key={`nested-question-no-${nestedIndex}`} className="nested-question">{nestedQuestion}</div>
                  );
                })}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Flowchart;
