import React, { useState, useEffect } from 'react';

const API_URL = "http://localhost:5001/fetchalldata";


const Tree = ({ data }) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const handleToggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const renderTreeNode = (node, key) => {
    const hasChildren = typeof node === 'object' && node !== null && Object.keys(node).length > 0;

    if (!hasChildren) {
      return <li key={key}>{node}</li>;
    }

    return (
      <li key={key}>
        <button onClick={handleToggleExpand}>{isExpanded ? '-' : '+'}</button>
        {key}
        {isExpanded && (
          <ul>
            {Object.entries(node).map(([childKey, childNode]) =>
              renderTreeNode(childNode, childKey)
            )}
          </ul>
        )}
      </li>
    );
  };

  return (
    <ul>
      {Object.entries(data).map(([key, node]) => renderTreeNode(node, key))}
    </ul>
  );
};

export default Tree;
