import React, { useState } from 'react';

function QATopic(props) {
  const [topic, setTopic] = useState('');
  const [qaList, setQAList] = useState([{ question: '', answer: '', nestedQA: [{question: '', answer: ''}]}]);

  const handleTopicChange = (e) => {
    setTopic(e.target.value);
  };

  const handleQAChange = (index, field, value) => {
    const newList = [...qaList];
    newList[index][field] = value;
    setQAList(newList);
  };

  const handleNestedQAChange = (qaIndex, nestedIndex, field, value) => {
    const newList = [...qaList];
    newList[qaIndex].nestedQA[nestedIndex][field] = value;
    setQAList(newList);
  };

  const handleAddQA = () => {
    setQAList([...qaList, { question: '', answer: '', nestedQA: [{ question: '', answer: '' }] }]);
  };

  const handleAddNestedQA = (qaIndex) => {
    const newList = [...qaList];
    newList[qaIndex].nestedQA.push({ question: '', answer: '' });
    setQAList(newList);
  };

  return (
    <div>
      <label>
        Topic:
        <input type="text" value={topic} onChange={handleTopicChange} />
      </label>
      {qaList.map((qa, qaIndex) => (
        <div key={qaIndex}>
          <label>
            Question:
            <input
              type="text"
              value={qa.question}
              onChange={(e) => handleQAChange(qaIndex, 'question', e.target.value)}
            />
          </label>
          <label>
            Answer:
            <input
              type="text"
              value={qa.answer}
              onChange={(e) => handleQAChange(qaIndex, 'answer', e.target.value)}
            />
          </label>
          {qa.nestedQA.map((nestedQA, nestedIndex) => (
            <div key={nestedIndex}>
              <label>
                Nested Question:
                <input
                  type="text"
                  value={nestedQA.question}
                  onChange={(e) => handleNestedQAChange(qaIndex, nestedIndex, 'question', e.target.value)}
                />
              </label>
              <label>
                Nested Answer:
                <input
                  type="text"
                  value={nestedQA.answer}
                  onChange={(e) => handleNestedQAChange(qaIndex, nestedIndex, 'answer', e.target.value)}
                />
              </label>
            </div>
          ))}
          <button onClick={() => handleAddNestedQA(qaIndex)}>Add Nested QA</button>
        </div>
      ))}
      <button onClick={handleAddQA}>Add New QA</button>
    </div>
  );
}

export default QATopic;
