import './addflow.css';

import React, { useState } from "react";
import "./styles.css";

const QuestionAnswerForm = () => {
  const [questions, setQuestions] = useState([
    { question: "", nestedQuestions: [] }
  ]);

  const addQuestion = () => {
    setQuestions([...questions, { question: "", nestedQuestions: [] }]);
  };

  const handleQuestionChange = (event, index) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].question = event.target.value;
    setQuestions(updatedQuestions);
  };

  const addNestedQuestion = (questionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].nestedQuestions.push("");
    setQuestions(updatedQuestions);
  };

  const handleNestedQuestionChange = (event, questionIndex, nestedQuestionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].nestedQuestions[nestedQuestionIndex] = event.target.value;
    setQuestions(updatedQuestions);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const dataToSend = JSON.stringify(questions);
    console.log(dataToSend);
    // send data to API here
  };

  return (
    <div className="question-form-container">
      <form onSubmit={handleSubmit}>
        {questions.map((question, questionIndex) => (
          <div key={questionIndex}>
            <div className="question-field">
              <label>Question:</label>
              <input type="text" value={question.question} onChange={(event) => handleQuestionChange(event, questionIndex)} />
            </div>
            <div className="nested-question-field">
              {question.nestedQuestions.map((nestedQuestion, nestedQuestionIndex) => (
                <div key={nestedQuestionIndex} className="nested-question">
                  <label>Nested Question:</label>
                  <input type="text" value={nestedQuestion} onChange={(event) => handleNestedQuestionChange(event, questionIndex, nestedQuestionIndex)} />
                </div>
              ))}
              <button type="button" onClick={() => addNestedQuestion(questionIndex)}>Add Nested Question</button>
            </div>
          </div>
        ))}
        <button type="button" onClick={addQuestion}>Add Question</button>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default QuestionAnswerForm;
