import React, { useState, useEffect } from 'react';

const API_URL = "http://localhost:5001/fetchalldata";

const JsonTree = ({ data }) => {
  const [expandedBranches, setExpandedBranches] = useState({});

  const handleToggleExpand = (key) => {
    setExpandedBranches((prevState) => {
      return {
        ...prevState,
        [key]: !prevState[key]
      };
    });
  };

  const renderTreeNode = (node, key) => {
    const hasChildren = typeof node === 'object' && node !== null && Object.keys(node).length > 0;
    const isExpanded = expandedBranches[key];

    if (!hasChildren) {
      return <li key={key}>{node}</li>;
    }

    return (
      <li key={key}>
        <button onClick={() => handleToggleExpand(key)}>{isExpanded ? '-' : '+'}</button>
        {key}
        {isExpanded && (
          <ul>
            {Object.entries(node).map(([childKey, childNode]) =>
              renderTreeNode(childNode, childKey)
            )}
          </ul>
        )}
      </li>
    );
  };

  return (
    <ul>
      {Object.entries(data).map(([key, node]) => renderTreeNode(node, key))}
    </ul>
  );
};

export default JsonTree;
