import React, { useState } from 'react';
import './styles.css';

const QuestionForm = () => {
  const [topic, setTopic] = useState('');
  const [questions, setQuestions] = useState([{ question: '', nestedQuestions: [] }]);

  const handleTopicChange = (event) => {
    setTopic(event.target.value);
  };

  const handleQuestionChange = (event, index) => {
    const { name, value } = event.target;
    const list = [...questions];
    list[index][name] = value;
    setQuestions(list);
  };

  const handleNestedQuestionChange = (event, index, nestedIndex) => {
    const { name, value } = event.target;
    const list = [...questions];
    list[index].nestedQuestions[nestedIndex][name] = value;
    setQuestions(list);
  };

  const handleAddQuestion = () => {
    setQuestions([...questions, { question: '', nestedQuestions: [] }]);
  };

  const handleAddNestedQuestion = (index) => {
    const list = [...questions];
    list[index].nestedQuestions.push({ condition: '', nestedQuestion: '' });
    setQuestions(list);
  };

  const handleRemoveQuestion = (index) => {
    const list = [...questions];
    list.splice(index, 1);
    setQuestions(list);
  };

  const handleRemoveNestedQuestion = (index, nestedIndex) => {
    const list = [...questions];
    list[index].nestedQuestions.splice(nestedIndex, 1);
    setQuestions(list);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      topic: topic,
      questions: questions.map((q) => ({
        ques: q.question,
        condition: q.nestedQuestions.length > 0 ? { yes: q.nestedQuestions.map((nq) => nq.condition) } : null,
        nestedQuestions: q.nestedQuestions.map((nq) => ({ nestedQuestion: nq.nestedQuestion })),
      })),
    };
    console.log(JSON.stringify(data));
    // Send data to API endpoint using fetch() or Axios
  };

  return (
    <div className="container">
        <h1>Topic: {topic}</h1>
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label>Question:</label>
                <input type="text" className="form-control" value={question} onChange={(e) => setQuestion(e.target.value)} required />
            </div>
            {nestedQuestions.map((nestedQ, index) => (
                <div className="form-group" key={index}>
                    <label>Nested Question:</label>
                    <div className="row">
                        <div className="col-md-9">
                            <input type="text" className="form-control" value={nestedQ.nestedQuestion} onChange={(e) => handleNestedQuestionChange(e, index)} required />
                        </div>
                        <div className="col-md-3">
                            <button type="button" className="btn btn-danger" onClick={() => handleRemoveNestedQuestion(index)}>Remove</button>
                        </div>
                    </div>
                    <div className="form-group mt-2">
                        <label>Condition:</label>
                        <input type="text" className="form-control" value={nestedQ.condition} onChange={(e) => handleConditionChange(e, index)} />
                    </div>
                </div>
            ))}
            <button type="button" className="btn btn-primary mt-2 mb-2" onClick={handleAddNestedQuestion}>Add Nested Question</button>
            <button type="submit" className="btn btn-success">Submit</button>
        </form>
    </div>
);

}
