import React, { useState } from 'react';
import './addflow.css';

const QATopic = ({ topic }) => {
  const [qas, setQAs] = useState([
    {
      question: '',
      answer: '',
      nestedQAs: [],
    },
  ]);

  const handleAddQA = () => {
    setQAs([...qas, { question: '', answer: '', nestedQAs: [] }]);
  };

  const handleAddNestedQA = (qaIndex) => {
    const newQAs = [...qas];
    newQAs[qaIndex].nestedQAs.push({ question: '', answer: '' });
    setQAs(newQAs);
  };

  const handleQAChange = (index, key, value) => {
    const newQAs = [...qas];
    newQAs[index][key] = value;
    setQAs(newQAs);
  };

  const handleNestedQAChange = (qaIndex, nestedIndex, key, value) => {
    const newQAs = [...qas];
    newQAs[qaIndex].nestedQAs[nestedIndex][key] = value;
    setQAs(newQAs);
  };

  return (
    <div className="container">
      <h2>{topic}</h2>
      {qas.map((qa, qaIndex) => (
        <div key={qaIndex} className="qaContainer">
          <label className="inputContainer">
            <span className="label">Question:</span>
            <input
              type="text"
              value={qa.question}
              onChange={(e) => handleQAChange(qaIndex, 'question', e.target.value)}
            />
          </label>
          <label className="inputContainer">
            <span className="label">Answer:</span>
            <input
              type="text"
              value={qa.answer}
              onChange={(e) => handleQAChange(qaIndex, 'answer', e.target.value)}
            />
          </label>
          {qa.nestedQAs.map((nestedQA, nestedIndex) => (
            <div key={nestedIndex} className="nestedQAContainer">
              <label className="inputContainer">
                <span className="label">Question:</span>
                <input
                  type="text"
                  value={nestedQA.question}
                  onChange={(e) => handleNestedQAChange(qaIndex, nestedIndex, 'question', e.target.value)}
                />
              </label>
              <label className="inputContainer">
                <span className="label">Answer:</span>
                <input
                  type="text"
                  value={nestedQA.answer}
                  onChange={(e) => handleNestedQAChange(qaIndex, nestedIndex, 'answer', e.target.value)}
                />
              </label>
            </div>
          ))}
          <button className="button" onClick={() => handleAddNestedQA(qaIndex)}>Add Nested QA</button>
        </div>
      ))}
      <button className="button" onClick={handleAddQA}>Add QA</button>
    </div>
  );
};

export default QATopic;
