import './addflow.css';

import React, { useState } from 'react';
import axios from 'axios';

function QuestionForm() {
  const [question, setQuestion] = useState('');
  const [nestedQuestions, setNestedQuestions] = useState(['']);

  const handleQuestionChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleNestedQuestionChange = (index, event) => {
    const newNestedQuestions = [...nestedQuestions];
    newNestedQuestions[index] = event.target.value;
    setNestedQuestions(newNestedQuestions);
  };

  const handleAddNestedQuestion = () => {
    const newNestedQuestions = [...nestedQuestions, ''];
    setNestedQuestions(newNestedQuestions);
  };

  const handleSubmit = () => {
    const data = { question, nestedQuestions };
    axios.post('http://127.0.0.1:5001/addflowapi', data)
      .then(response => console.log(response))
      .catch(error => console.log(error));
  };

  return (
    <div className="question-form-container">
      <div className="question-field">
        <label>Question</label>
        <input type="text" value={question} onChange={handleQuestionChange} />
      </div>
      {nestedQuestions.map((nestedQuestion, index) => (
        <div key={index} className="nested-question">
          <div className="question-field">
            <label>Nested Question</label>
            <input
              type="text"
              value={nestedQuestion}
              onChange={(event) => handleNestedQuestionChange(index, event)}
              className="nested-question-field"
            />
          </div>
          {index === nestedQuestions.length - 1 && (
            <button className="add-nested-question" onClick={handleAddNestedQuestion}>
              Add Nested Question
            </button>
          )}
        </div>
      ))}
      <div className="submit-button-container">
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
}

export default QuestionForm;
