import React, { useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:5001/addflowapi";

function QuestionForm() {
  const [topic, setTopic] = useState("");
  const [questions, setQuestions] = useState([
    { question: "", nestedQuestions: [] }
  ]);

  const handleChange = (e, index, nestedIndex) => {
    const { name, value } = e.target;
    if (nestedIndex !== undefined) {
      const nestedQuestions = [...questions[index].nestedQuestions];
      nestedQuestions[nestedIndex][name] = value;
      setQuestions([
        ...questions.slice(0, index),
        { ...questions[index], nestedQuestions },
        ...questions.slice(index + 1)
      ]);
    } else {
      setQuestions([
        ...questions.slice(0, index),
        { ...questions[index], [name]: value },
        ...questions.slice(index + 1)
      ]);
    }
  };

  const handleAddQuestion = () => {
    setQuestions([...questions, { question: "", nestedQuestions: [] }]);
  };

  const handleSubmit = async () => {
    const data = {
      topic,
      questions
    };
    try {
      const response = await axios.post(API_URL, data);
      console.log(response);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="question-form-container">
      <h1>Question Form</h1>
      <div className="form">
        <label>
          Topic:
          <input type="text" value={topic} name="topic" onChange={(e) => setTopic(e.target.value)} />
        </label>
        {questions.map((q, i) => (
          <div key={i}>
            <label>
              Question {i + 1}:
              <input
                type="text"
                value={q.question}
                name="question"
                onChange={(e) => handleChange(e, i)}
              />
            </label>
            <br />
            {q.nestedQuestions.map((nq, j) => (
              <div key={j}>
                <label>
                  Nested Question {j + 1}:
                  <input
                    type="text"
                    value={nq.nestedQuestion}
                    name="nestedQuestion"
                    onChange={(e) => handleChange(e, i, j)}
                  />
                </label>
                <br />
              </div>
            ))}
            <button onClick={() => handleChange({ target: { name: "nestedQuestions", value: [...q.nestedQuestions, { nestedQuestion: "" }] } }, i)}>Add Nested Question</button>
          </div>
        ))}
        <button onClick={handleAddQuestion}>Add Question</button>
        <br />
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
}

export default QuestionForm;
