import ReactFlowRenderer from "./graphs/ReactFlowRenderer";
// import {nodes, edges} from "./graphs/fever_elements";

const nodes = []
const edges = []

const Dropdown = ({ options }) => {
  const [selectedOption, setSelectedOption] = useState("");
  const [data, setData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleOptionSelect = async (option) => {
    setSelectedOption(option.label);
    setIsLoading(true);

    try {
      const response = await fetch(`http://localhost:5001/graphapi`, {
        method:'POST',
        headers : {
          'Accept' :'application/json',
          'Content-Type' : 'application/json'
        },
        body : JSON.stringify({
          "label": option.label
        })
      });
      const data = await response.json();
      
      const nodes = data.nodes.map(node => ({
        id: node.id,
        data: { label: node.label },
        position: { x: node.x, y: node.y }
      }));

      const edges = data.edges.map(edge => ({
        id: edge.id,
        source: edge.source,
        target: edge.target
      }));

      setData(data.nodes);
      
    } catch (error) {
      console.error(error);
    }

    setIsLoading(false);
  };

  return (
    <div>
      <select
        value={selectedOption}
        onChange={(e) =>
          handleOptionSelect(
            options.find((option) => option.label === e.target.value)
          )
        }
      >
        <option value="">Select an option</option>
        {options.map((option) => (
          <option key={option.label} value={option.label}>
            {option.label}
          </option>
        ))}
      </select>
      {isLoading && <div>Loading...</div>}
      {data && <div>{JSON.stringify(nodes)}</div>}
    
      <ReactFlowRenderer initialNodes={data.nodes} initialEdges={edges}/> 

    </div>
  );
};

export default Dropdown;
