import React, { useState } from "react";
import './flowtree.css';

const Node = ({ nodeData }) => {
  const [showDetails, setShowDetails] = useState(false);

  const handleAnswer = (answer) => {
    if (answer === "yes") {
      setShowDetails(true);
    } else {
      setShowDetails(false);
    }
  }

  return (
    <div className="node">
      <div className="question">{nodeData.ques}</div>
      {nodeData.condition && (
        <>
          <div className="answer">
            <button onClick={() => handleAnswer("yes")}>Yes</button>
            <button onClick={() => handleAnswer("no")}>No</button>
          </div>
          {showDetails && (
            <div className="details">
              {nodeData.condition.yes && (
                <div className="details-yes">
                  {nodeData.condition.yes.map((ques, index) => (
                    <Node key={index} nodeData={{ ques }} />
                  ))}
                </div>
              )}
              {nodeData.condition.no && (
                <div className="details-no">
                  {nodeData.condition.no.map((ques, index) => (
                    <Node key={index} nodeData={{ ques }} />
                  ))}
                </div>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

const FlowChart = ({ data }) => {
  return (
    <div className="flow-chart">
      <Node nodeData={{ ques: data[0].symptom }} />
      <div className="arrow-down"></div>
      <div className="details">
        {data[0].questions.map((ques, index) => (
          <Node key={index} nodeData={ques} />
        ))}
      </div>
    </div>
  );
};

export default FlowChart;
